$(document).ready(function(){$("form").on("submit",function(){var t=$("form input"),o=$("form textarea"),n={item:t.val(),notes:o.val()};return $.ajax({type:"POST",url:"/add",data:n,success:function(t){location.replace("http://localhost:3000/")}}),!1});var t=$("li"),o=t.find('input[type="checkbox"]');$(o).on("click",function(){var t=window.confirm("Are you sure you want to delete this todo ?");if(t===!0){var o=$(this).attr("data-id");$.ajax({type:"DELETE",url:"/"+o,success:function(t){location.reload()}})}}),t.on("click",function(){$(this).toggleClass("open"),$(this).siblings("li").removeClass("open")})});